class ConfigManager:
    def __init__(self):
        self.config = configparser.ConfigParser()
        self.config_file = 'config.ini'
        if not os.path.exists(self.config_file):
            self.create_default_config()
        self.config.read(self.config_file)

    def create_default_config(self):
        self.config['DATABASE'] = {
            # 'host': '172.29.52.87',
            # 'port': '27017',
            # 'database': 'disposisi'
            'host': 'localhost',
            'port': '27017',
            'database': 'disposisi'
        }
        with open(self.config_file, 'w') as configfile:
            self.config.write(configfile)

    def get_database_config(self):
        return {
            'host': self.config['DATABASE']['host'],
            'port': int(self.config['DATABASE']['port']),
            'database': self.config['DATABASE']['database']
        }

    def save_database_config(self, host, port, database):
        self.config['DATABASE']['host'] = host
        self.config['DATABASE']['port'] = str(port)
        self.config['DATABASE']['database'] = database
        with open(self.config_file, 'w') as configfile:
            self.config.write(configfile)

class PDFRenderer:

    @classmethod
    def render_pdf(cls, data, output_filename='output_disposisi.pdf', ukuran_kertas='A4'):
        if ukuran_kertas == 'A4':
            pagesize = A4
            margin = 0.5 * cm
        else:  # F4
            pagesize = (215 * mm, 330 * mm)  # Ukuran F4
            margin = 0.7 * cm

        doc = SimpleDocTemplate(output_filename, pagesize=pagesize, 
                                topMargin=margin, bottomMargin=margin, 
                                leftMargin=margin, rightMargin=margin)
        elements = []
        
        # Perbesar ukuran font untuk header
        header_font_size = 12 if ukuran_kertas == 'A4' else 16
        
        # Tambahkan header
        elements.append(cls._create_header(data['jenis_dokumen'], header_font_size))
        elements.append(Spacer(1, 0.2*cm))
        
        # Tambahkan data table dengan ukuran font yang lebih besar
        data_table = cls._create_data_table(data, ukuran_kertas)
        elements.append(data_table)
        elements.append(Spacer(1, 0.2*cm))
        
        # Tambahkan disposition table
        disposition_table = cls._create_disposition_table(ukuran_kertas)
        elements.append(disposition_table)
        
        # Hitung ruang yang tersisa untuk kotak catatan
        total_height = pagesize[1] - 2*margin
        used_height = sum(element.wrap(pagesize[0] - 2*margin, total_height)[1] for element in elements)
        remaining_height = total_height - used_height - 0.5*cm
        
        # Buat kotak catatan dengan tinggi yang dihitung
        notes_box = cls._create_notes_box(remaining_height)
        elements.append(notes_box)
        
        doc.build(elements)
        
    @classmethod
    def _create_header(cls, jenis_dokumen, font_size):
        
        bsg_logo_base64 = "/data:image/jpeg;base64,"
        bpd_logo_base64 = ""

        bsg_logo = cls._get_image_from_base64(bsg_logo_base64, max_width=2.5*cm, max_height=2.5*cm)
        bpd_logo = cls._get_image_from_base64(bpd_logo_base64, max_width=2.5*cm, max_height=2.5*cm)

        
        is_memo = 'Memo' in jenis_dokumen
        icon = cls._create_icon(is_memo, width=0.5*cm, height=0.5*cm)

        styles = getSampleStyleSheet()
        header_style = ParagraphStyle('Header', parent=styles['Heading1'], fontName='Times-Bold', fontSize=font_size, textColor=colors.black, alignment=1, spaceAfter=6)
        header_text = 'LEMBAR DISPOSISI DIVISI TEKNOLOGI INFORMASI'
        
        # Create a table for the icon and text
        header_content = Table(
            [[icon, Paragraph(header_text, header_style)]],
            colWidths=[0.7*cm, 11.3*cm],
            rowHeights=[max(bsg_logo._height, bpd_logo._height)]  # Use _height instead of height
        )
        header_content.setStyle(TableStyle([
            ('ALIGN', (0,0), (0,0), 'RIGHT'),  # Align icon to the right
            ('ALIGN', (1,0), (1,0), 'LEFT'),   # Align text to the left
            ('VALIGN', (0,0), (-1,-1), 'MIDDLE'),
            ('TOPPADDING', (1,0), (1,0), 0.3*cm),
        ]))

        header_data = [[bsg_logo, header_content, bpd_logo]]
        header_table = Table(header_data, colWidths=[3*cm, 12*cm, 3*cm])
        header_table.setStyle(TableStyle([
            ('ALIGN', (0,0), (0,0), 'LEFT'),    # Align BSG logo to the left
            ('ALIGN', (1,0), (1,0), 'CENTER'),  # Center the header content
            ('ALIGN', (2,0), (2,0), 'RIGHT'),   # Align BPD logo to the right
            ('VALIGN', (0,0), (-1,-1), 'MIDDLE'),
        ]))
        
        return header_table

    @classmethod
    def _create_icon(cls, is_memo, width=0.8*cm, height=0.8*cm):
        from reportlab.graphics.shapes import Drawing, Path
        from reportlab.lib.colors import Color

        d = Drawing(width, height)
        
        # Define colors
        icon_red = Color(0.95, 0.2, 0.2)    # A bright, soft red
        icon_blue = Color(0.2, 0.6, 0.9)    # A bright, soft blue
        
        if is_memo:
            # Red memo icon
            p = Path(strokeColor=icon_red, fillColor=None, strokeWidth=1.5)
            p.moveTo(2, 2)
            p.lineTo(18, 2)
            p.lineTo(18, 18)
            p.lineTo(2, 18)
            p.closePath()
            p.moveTo(5, 6)
            p.lineTo(15, 6)
            p.moveTo(5, 10)
            p.lineTo(15, 10)
            p.moveTo(5, 14)
            p.lineTo(12, 14)
        else:
            # Blue envelope icon
            p = Path(strokeColor=icon_blue, fillColor=None, strokeWidth=1.5)
            # Draw the envelope body
            p.moveTo(2, 15)
            p.lineTo(2, 5)
            p.lineTo(18, 5)
            p.lineTo(18, 15)
            p.lineTo(2, 15)
            # Draw the envelope flap
            p.moveTo(2, 15)
            p.lineTo(10, 10)
            p.lineTo(18, 15)
        
        d.add(p)
        d.scale(width/20, height/20)  # Scale the drawing to the desired size
        return d

    @classmethod
    def _create_data_table(cls, data, ukuran_kertas):
        base_font_size = 12 if ukuran_kertas == 'A4' else 14
        styles = getSampleStyleSheet()
        
        # Define the base style
        base_style = ParagraphStyle(
            'Base',
            fontName='Times-Roman',
            fontSize=base_font_size,  # Ukuran font diubah menjadi 12
            leading=14,   # Sesuaikan leading
        )
        
        # Define normal dan bold styles
        normal_style = ParagraphStyle('Normal', parent=base_style)
        bold_style = ParagraphStyle('Bold', parent=base_style, fontName='Times-Bold')
        
        # Definisikan gaya label yang konsisten
        label_style = ParagraphStyle('Label', parent=normal_style, alignment=0, spaceAfter=0, spaceBefore=0)
        
        # Gaya khusus untuk label Perihal
        perihal_label_style = ParagraphStyle('PerihalLabel', parent=label_style, valign='TOP')
        
        # Gaya untuk nilai Perihal
        perihal_value_style = ParagraphStyle('PerihalValue', parent=bold_style, spaceAfter=0, spaceBefore=0)

        def create_paragraph(text, is_value=False, is_perihal=False):
            if is_perihal:
                return Paragraph(str(text), perihal_value_style if is_value else perihal_label_style)
            return Paragraph(str(text), bold_style if is_value else label_style)

        is_memo = 'Memo' in data['jenis_dokumen']
        doc_type = 'Memo' if is_memo else 'Surat'

        left_data = [
            ['1.', f'{doc_type} Dari', ':', create_paragraph(data['memo_dari'], True)],
            ['2.', f'Tanggal {doc_type}', ':', create_paragraph(data['tanggal_memo'], True)],
            ['3.', f'Nomor {doc_type}', ':', create_paragraph(data['nomor_memo'], True)],
            ['4.', 'Lampiran', ':', create_paragraph(data['lampiran'], True)],
            ['5.', create_paragraph('Perihal', is_perihal=True), ':', create_paragraph(data['perihal'], True, is_perihal=True)]
        ]
        right_data = [
            ['6.', 'Tanggal Terima', ':', create_paragraph(data['tanggal_terima'], True)],
            ['7.', 'Indeks', ':', create_paragraph(data['indeks'], True)],
            ['8.', 'Sifat', ':', create_paragraph(data['sifat'], True)],
            ['9.', 'Paraf Petugas', ':', create_paragraph(data['paraf_petugas'], True)],
            ['10.', 'Kode', ':', create_paragraph(data['kode'], True)]
        ]
        
        left_table = Table(left_data, colWidths=[0.6*cm, 2.75*cm, 0.4*cm, 7.5*cm])
        right_table = Table(right_data, colWidths=[0.6*cm, 2.75*cm, 0.4*cm, 4.5*cm])
        
