    @classmethod
    def _get_image_from_base64(cls, base64_string, max_width=2.5*cm, max_height=2.5*cm):
        try:
            # Remove the data URL prefix if present
            if ',' in base64_string:
                base64_string = base64_string.split(',', 1)[1]
            
            image_data = base64.b64decode(base64_string)
            img = PILImage.open(BytesIO(image_data))
            img = img.convert('RGB')
            img_width, img_height = img.size
            
            # Calculate scaling factor to fit within max dimensions while preserving aspect ratio
            scale = min(max_width/img_width, max_height/img_height)
            new_width = img_width * scale
            new_height = img_height * scale

            img_buffer = BytesIO()
            img.save(img_buffer, format='PNG')
            img_buffer.seek(0)

            return Image(img_buffer, width=new_width, height=new_height)
        except Exception as e:
            print(f"Error processing base64 image: {e}")
            return Paragraph("Logo", getSampleStyleSheet()['Normal'])

