# Function to format date in Indonesian
def format_tanggal_indonesia(tanggal_str):
    bulan_indonesia = {
        "01": "Januari", "02": "Februari", "03": "Maret", "04": "April",
        "05": "Mei", "06": "Juni", "07": "Juli", "08": "Agustus",
        "09": "September", "10": "Oktober", "11": "November", "12": "Desember"
    }
    tanggal = datetime.strptime(tanggal_str, '%Y-%m-%d')
    return f"{tanggal.day} {bulan_indonesia[tanggal.strftime('%m')]} {tanggal.year}"

class AutocompleteEntry(ttk.Entry):
    def __init__(self, master, column_name, db_manager, *args, **kwargs):
        super().__init__(master, *args, **kwargs)
        self.column_name = column_name
        self.db_manager = db_manager
        self.listbox_window = None
        self.listbox = None
        self.bind('<KeyRelease>', self.on_keyrelease)
        self.bind('<FocusOut>', self.on_focus_out)
        self.bind('<Down>', self.move_selection_down)
        self.bind('<Up>', self.move_selection_up)
        self.bind('<Return>', self.use_selection)

    def destroy_listbox(self):
        if self.listbox_window:
            self.listbox_window.destroy()
            self.listbox_window = None
            self.listbox = None

    def on_focus_out(self, event):
        self.after(100, self.destroy_listbox)

    def on_keyrelease(self, event):
        if event.keysym in ('Up', 'Down', 'Return'):
            return
        self.destroy_listbox()
        
        value = self.get().lower()
        if not value:
            return

        data = self.get_data(value)
        if not data:
            return

        self.create_listbox(data)

    def create_listbox(self, data):
        self.listbox_window = tk.Toplevel(self.master)
        self.listbox_window.overrideredirect(True)
        self.listbox_window.attributes('-topmost', True)

        self.listbox = tk.Listbox(self.listbox_window)
        self.listbox.pack(fill=tk.BOTH, expand=True)
        self.listbox.bind('<<ListboxSelect>>', self.on_select)

        x = self.master.winfo_rootx() + self.winfo_x()
        y = self.master.winfo_rooty() + self.winfo_y() + self.winfo_height()
        width = self.winfo_width()
        height = min(len(data) * 24, 200)  # Adjust height based on number of items
        self.listbox_window.geometry(f"{width}x{height}+{x}+{y}")

        for item in data:
            self.listbox.insert(tk.END, item)

        if self.listbox.size() > 0:
            self.listbox.select_set(0)

    def on_select(self, event=None):
        if self.listbox:
            try:
                selected = self.listbox.get(self.listbox.curselection())
                self.delete(0, tk.END)
                self.insert(0, selected)
            except tk.TclError:
                pass
            finally:
                self.destroy_listbox()

    def move_selection_down(self, event):
        if self.listbox:
            current = self.listbox.curselection()
            if current:
                index = current[0]
                if index < self.listbox.size() - 1:
                    self.listbox.selection_clear(0, tk.END)
                    self.listbox.selection_set(index + 1)
                    self.listbox.see(index + 1)
            else:
                self.listbox.selection_set(0)

    def move_selection_up(self, event):
        if self.listbox:
            current = self.listbox.curselection()
            if current:
                index = current[0]
                if index > 0:
                    self.listbox.selection_clear(0, tk.END)
                    self.listbox.selection_set(index - 1)
                    self.listbox.see(index - 1)

    def use_selection(self, event):
        if self.listbox:
            self.on_select()
        else:
            self.event_generate("<<Return>>")

    def get_data(self, value):
        try:
            # Escape karakter khusus regex
            escaped_value = re.escape(value)
            query = {self.column_name: {"$regex": f"^{escaped_value}", "$options": "i"}}
            projection = {self.column_name: 1, "_id": 0}
            data = self.db_manager.collection.find(query, projection).limit(10)
            return [item[self.column_name] for item in data]
        except Exception as e:
            logging.error(f"Error fetching autocomplete data: {e}")
            return []
        
class DatabaseManager:
    def __init__(self, config):
        try:
            # self.client = MongoClient(config['host'], config['port'],  serverSelectionTimeoutMS=5000)
            # mongo_client = MongoClient(f'mongodb://localhost:27017/',  serverSelectionTimeoutMS=5000)
            self.client = MongoClient(f'mongodb://localhost:27017/',  serverSelectionTimeoutMS=5000)
            # self.client = MongoClient(f'mongodb://10.10.81.189:27017/',  serverSelectionTimeoutMS=5000)
            # self.client = MongoClient(f'mongodb://10.10.81.189:27017/',  serverSelectionTimeoutMS=5000)
            self.client.server_info()  # Akan raise exception jika tidak bisa terhubung
            self.db = self.client[config['database']]
            self.collection = self.db['disposisi']
            self.fs = gridfs.GridFS(self.db)
            # logging.info("Berhasil terhubung ke MongoDB")
        except Exception as e:
            logging.error(f"Gagal terhubung ke MongoDB: {e}")
            raise

    def check_connection(self):
        try:
            self.client.server_info()
            # logging.info("Koneksi database OK")
            return True
        except Exception as e:
            logging.error(f"Koneksi database terputus: {e}")
            return False

    def insert_data(self, data):
        if not self.check_connection():
            logging.error("Gagal menyimpan data: Koneksi database terputus")
            return None
        try:
            result = self.collection.insert_one(data)
            # logging.info(f"Data berhasil disimpan dengan ID: {result.inserted_id}")
            return result.inserted_id
        except Exception as e:
            logging.error(f"Gagal menyimpan data: {e}")
            logging.error(traceback.format_exc())
            return None

    def update_data(self, item_id, data):
        if not self.check_connection():
            logging.error("Gagal memperbarui data: Koneksi database terputus")
            return None
        try:
            result = self.collection.update_one({'_id': ObjectId(item_id)}, {'$set': data})
            # logging.info(f"Update result: matched={result.matched_count}, modified={result.modified_count}")
            return result.modified_count
        except Exception as e:
            logging.error(f"Gagal memperbarui data: {e}")
            logging.error(traceback.format_exc())
            return None

    def fetch_data(self):
        return list(self.collection.find())

    def delete_data(self, item_id):
        self.collection.delete_one({'_id': ObjectId(item_id)})

    def close(self):
        self.client.close()

    def backup_database(self):
        backup_dir = 'database_backups'
        if not os.path.exists(backup_dir):
            os.makedirs(backup_dir)
        timestamp = datetime.now().strftime('%Y%m%d_%H%M%S')
        backup_path = f"{backup_dir}/disposisi_dokumen_backup_{timestamp}"
        
        # Perform MongoDB dump
        os.system(f"mongodump --out {backup_path}")
        
        # Create a metadata file with the current document count
        metadata = {
            'document_count': self.collection.count_documents({}),
            'backup_time': timestamp
        }
        with open(os.path.join(backup_path, 'metadata.json'), 'w') as f:
            json.dump(metadata, f)
        
        print(f"Database backed up to {backup_path}")

    def restore_from_latest_backup(self):
        backup_dir = 'database_backups'
        if not os.path.exists(backup_dir):
            print("No backups found.")
            return False
        
        backup_folders = [f for f in os.listdir(backup_dir) if os.path.isdir(os.path.join(backup_dir, f))]
        if not backup_folders:
            print("No backup folders found.")
            return False
        
        latest_backup = max(backup_folders, key=lambda x: os.path.getctime(os.path.join(backup_dir, x)))
        latest_backup_path = os.path.join(backup_dir, latest_backup)
        
        # Perform MongoDB restore
        os.system(f"mongorestore {latest_backup_path}")
        
        print(f"Database restored from {latest_backup_path}")
        return True

    def upload_file(self, file_path):
        with open(file_path, 'rb') as file:
            file_id = self.fs.put(file, filename=os.path.basename(file_path))
        return str(file_id)

