class ConfigDialog(tk.Toplevel):
    def __init__(self, parent, config_manager):
        super().__init__(parent)
        self.title("Konfigurasi Database")
        self.config_manager = config_manager
        self.create_widgets()

    def create_widgets(self):
        current_config = self.config_manager.get_database_config()

        ttk.Label(self, text="Host:").grid(row=0, column=0, padx=5, pady=5)
        self.host_entry = ttk.Entry(self)
        self.host_entry.insert(0, current_config['host'])
        self.host_entry.grid(row=0, column=1, padx=5, pady=5)

        ttk.Label(self, text="Port:").grid(row=1, column=0, padx=5, pady=5)
