    def add_template(self):
        template_name = self.template_name.get()
        if not template_name:
            messagebox.showerror("Error", "Nama template harus diisi!")
            return

        template_data = {
            "jenis_dokumen": self.jenis_dokumen.get(),
            "memo_dari": self.memo_dari.get(),
            "nomor_memo": self.nomor_memo.get(),
            "lampiran": self.lampiran.get(),
            "perihal": self.perihal.get(),
            "indeks": self.indeks.get(),
            "sifat": self.sifat.get(),
            "paraf_petugas": self.paraf_petugas.get(),
            "kode": self.kode.get()
        }

        self.template_manager.save_template(template_name, template_data)
        self.template_name.set("")  # Clear the template name entry
        self.update_template_list()
        messagebox.showinfo("Sukses", "Template berhasil ditambahkan!")

    def edit_template(self):
        selected_template = self.selected_template.get()
        if not selected_template:
            messagebox.showerror("Error", "Pilih template yang akan diedit!")
            return

        template_data = {
            "jenis_dokumen": self.jenis_dokumen.get(),
            "memo_dari": self.memo_dari.get(),
            "nomor_memo": self.nomor_memo.get(),
            "lampiran": self.lampiran.get(),
            "perihal": self.perihal.get(),
            "indeks": self.indeks.get(),
            "sifat": self.sifat.get(),
            "paraf_petugas": self.paraf_petugas.get(),
            "kode": self.kode.get()
        }

        self.template_manager.save_template(selected_template, template_data)
        messagebox.showinfo("Sukses", "Template berhasil diubah!")

    def delete_template(self):
        selected_template = self.selected_template.get()
        if not selected_template:
            messagebox.showerror("Error", "Pilih template yang akan dihapus!")
            return

        if messagebox.askyesno("Konfirmasi", f"Apakah Anda yakin ingin menghapus template '{selected_template}'?"):
            self.template_manager.delete_template(selected_template)
            self.update_template_list()
            messagebox.showinfo("Sukses", "Template berhasil dihapus!")

    def update_template_list(self):
        self.template_combobox['values'] = self.template_manager.get_template_names()
        self.selected_template.set('')  # Clear the selection

    def apply_template(self, event=None):
        selected_template_name = self.selected_template.get()
        if not selected_template_name:
            logging.info("No template selected")
            return

        template_data = self.template_manager.get_template(selected_template_name)
        if template_data:
            # logging.info(f"Applying template: {selected_template_name}")
            self.jenis_dokumen.set(template_data.get("jenis_dokumen", ""))
            self.memo_dari.delete(0, tk.END)
            self.memo_dari.insert(0, template_data.get("memo_dari", ""))
            self.nomor_memo.delete(0, tk.END)
            self.nomor_memo.insert(0, template_data.get("nomor_memo", ""))
            self.lampiran.delete(0, tk.END)
            self.lampiran.insert(0, template_data.get("lampiran", ""))
            self.perihal.delete(0, tk.END)
            self.perihal.insert(0, template_data.get("perihal", ""))
            self.indeks.delete(0, tk.END)
            self.indeks.insert(0, template_data.get("indeks", ""))
            self.sifat.delete(0, tk.END)
            self.sifat.insert(0, template_data.get("sifat", ""))
            self.paraf_petugas.delete(0, tk.END)
            self.paraf_petugas.insert(0, template_data.get("paraf_petugas", ""))
            self.kode.delete(0, tk.END)
            self.kode.insert(0, template_data.get("kode", ""))
            # logging.info("Template applied successfully")
        else:
            logging.warning(f"Template not found: {selected_template_name}")
            messagebox.showwarning("Template Tidak Ditemukan", f"Template '{selected_template_name}' tidak ditemukan.")

