    def search_data(self):
        search_text = self.search_var.get().lower().strip()
        sensitivity = self.sensitivity_scale.get()
        rows = self.db_manager.fetch_data()
        self.tree.delete(*self.tree.get_children())
        
        displayed_rows = 0
        total_rows = len(rows)
        
        for row in rows:
            if not search_text:
                # Jika input pencarian kosong, tampilkan semua data
                values = self._format_row_for_treeview(row)
                tag = self._get_color_tag(row.get('jenis_dokumen', ''))
                self.tree.insert("", "end", values=self._format_row_for_treeview(row), tags=(tag,))
                displayed_rows += 1
            else:
                # Cek setiap kolom
                match_found = False
                for key, value in row.items():
                    if key != '_id':  # Skip _id field
                        str_value = str(value).lower()
                        # Cek dengan fuzzy matching
                        if fuzz.partial_ratio(search_text, str_value) > sensitivity:
                            match_found = True
                            break
                        # Cek jika keyword ada di dalam string (untuk menangani kasus di tengah)
                        elif search_text in str_value:
                            match_found = True
                            break
                
                if match_found:
                    values = self._format_row_for_treeview(row)
                    tag = self._get_color_tag(row.get('jenis_dokumen', ''))
                    self.tree.insert("", "end", values=self._format_row_for_treeview(row), tags=(tag,))
                    displayed_rows += 1
        
        # Perbarui status pencarian
        self.update_status(f"Menampilkan {displayed_rows} dari {total_rows} item")

