    def _get_color_tag(self, jenis_dokumen):
        jenis_dokumen = jenis_dokumen.lower()
        if 'memo masuk' in jenis_dokumen:
            return 'memo_masuk'
        elif 'surat masuk' in jenis_dokumen:
            return 'surat_masuk'
        elif 'memo keluar' in jenis_dokumen:
            return 'memo_keluar'
        elif 'surat keluar' in jenis_dokumen:
            return 'surat_keluar'
        elif 'nota pertimbangan' in jenis_dokumen:
            return 'nota_pertimbangan'
        elif 'berita acara' in jenis_dokumen:
            return 'berita_acara'
        else:
            return '' 

