    def import_from_csv(self):
        try:
            file_path = filedialog.askopenfilename(filetypes=[("CSV files", "*.csv")])
            if not file_path:
                return

            with open(file_path, mode='r', encoding='utf-8') as file:
                reader = csv.reader(file)
                next(reader)  # Skip header row
                for row in reader:
                    # Pastikan row memiliki jumlah kolom yang benar
                    if len(row) >= 12:  # Minimal 12 kolom untuk data disposisi
                        data = row[1:13]  # Ambil kolom 1-12 (indeks 1-11)
                        self.db_manager.insert_data(data)
                    else:
                        print(f"Baris tidak valid: {row}")
                self.load_data()
            messagebox.showinfo("Sukses", "Data berhasil diimpor dari CSV!")
        except Exception as e:
            messagebox.showerror("Error", f"Gagal mengimpor dari CSV: {e}")

