    def check_latest_backup(self):
        backup_dir = 'database_backups'
        if not os.path.exists(backup_dir):
            return
        
        backup_folders = [f for f in os.listdir(backup_dir) if os.path.isdir(os.path.join(backup_dir, f))]
        if not backup_folders:
            return
        
        latest_backup = max(backup_folders, key=lambda x: os.path.getctime(os.path.join(backup_dir, x)))
        latest_backup_path = os.path.join(backup_dir, latest_backup)
        
        # Read the metadata file from the backup to get the document count at backup time
        metadata_file = os.path.join(latest_backup_path, 'metadata.json')
        if os.path.exists(metadata_file):
            with open(metadata_file, 'r') as f:
                metadata = json.load(f)
                backup_doc_count = metadata.get('document_count', 0)
            
            # Compare with current document count
            current_doc_count = self.db_manager.collection.count_documents({})
            
            if backup_doc_count > current_doc_count:
                self.ask_restore_with_timer(latest_backup_path)
        else:
            print("No metadata file found in the backup. Skipping backup check.")

    def ask_restore_with_timer(self, backup_path):
        restore_window = tk.Toplevel(self.master)
        restore_window.title("Backup Found")
        restore_window.geometry("300x150")

        label = ttk.Label(restore_window, text="A more recent backup was found.\nDo you want to restore from this backup?")
        label.pack(pady=10)

        timer_label = ttk.Label(restore_window, text="Time remaining: 5")
        timer_label.pack()

        button_frame = ttk.Frame(restore_window)
        button_frame.pack(pady=10)

        yes_button = ttk.Button(button_frame, text="Yes", command=lambda: self.restore_backup(backup_path, restore_window))
        yes_button.pack(side=tk.LEFT, padx=10)

        no_button = ttk.Button(button_frame, text="No", command=restore_window.destroy)
        no_button.pack(side=tk.LEFT, padx=10)

        def countdown(count):
            if count > 0:
                timer_label.config(text=f"Time remaining: {count}")
                restore_window.after(1000, countdown, count - 1)
            else:
                restore_window.destroy()

        countdown(5)

    def restore_backup(self, backup_path, window):
        window.destroy()
        if self.db_manager.restore_from_latest_backup():
            messagebox.showinfo("Restore Successful", "Database has been restored from the latest backup.")
            self.load_data()  # Refresh data setelah restore
        else:
            messagebox.showerror("Restore Failed", "Failed to restore from backup.")

